mapper.update:
      UPDATE passenger SET
					      genderID=#{genderID,jdbcType=INTEGER},
			              `name`=#{name,jdbcType=VARCHAR},
			              contactPhoneNumber=#{contactPhoneNumber,jdbcType=VARCHAR},
			              remarks=#{remarks,jdbcType=VARCHAR}
                   WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT p.*,c.attributeDetailsName AS genderName
 FROM passenger p   INNER JOIN attributedetails c ON p.genderID=c.id
  WHERE contactPhoneNumber=#{number,jdbcType=VARCHAR}
AND `name`=#{name,jdbcType=VARCHAR}
